(function(tinymce) {
  tinymce.PluginManager.add('lineheight', function(editor, url, $) {
    editor.on('init', function() {
      editor.formatter.register({
        lineheight: { inline: 'span', styles: { 'line-height': '%value' }}
      })
    })

    editor.addButton('lineheightselect', function() {
      const items = [
        {
          text: '1.0倍',
          value: '1em'
        },
        {
          text: '1.2倍',
          value: '1.2em'
        },
        {
          text: '1.5倍',
          value: '1.5em'
        },
        {
          text: '1.7倍',
          value: '1.7em'
        },
        {
          text: '2.0倍',
          value: '2em'
        }
      ]
      return {
        type: 'listbox',
        text: 'Line Height',
        tooltip: 'Line Height',
        values: items,
        fixedWidth: true,
        onPostRender: function() {
          var self = this
          editor.on('nodeChange', function(e) {
            var formatName = 'lineheight'
            var formatter = editor.formatter
            var value = null
            e.parents.forEach(function(node) {
              items.forEach(function(item) {
                if (formatName) {
                  if (formatter.matchNode(node, formatName, { value: item.value })) {
                    value = item.value
                  }
                } else {
                  if (formatter.matchNode(node, item.value)) {
                    value = item.value
                  }
                }
                if (value) {
                  return false
                }
              })
              if (value) {
                return false
              }
            })
            self.value(value)
          })
        },
        onselect: function(e) {
          tinymce.activeEditor.formatter.apply('lineheight', { value: this.value() })
        }
      }
    })
  })

  tinymce.PluginManager.requireLangPack('lineheight', 'de')
})(window.tinymce)

